import "./styles.css";

const apiKey = "XZSV35WMQY6RVV72FEXDUZ2R7";

const locationInput = document.querySelector("#location-input");
const searchButton = document.querySelector("#search-button");
const weatherLocation = document.querySelector("#location");
const weatherTemp = document.querySelector("#temp");
const weatherIcon = document.querySelector("#icon");
const weatherDescription = document.querySelector("#description");
const weatherHumidity = document.querySelector("#humidity");
const weatherWindSpeed = document.querySelector("#wind-speed");

async function getWeatherData(location) {
  try {
    let response = await fetch(
      `https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?unitGroup=metric&key=${apiKey}&contentType=json&iconSet=icons2`
    );
    if (!response.ok) {
      console.log(`Error Code: ${response.status}`);
    }
    let weatherData = await response.json();
    displayWeatherData(processWeatherData(weatherData));
  } catch {
    console.log(`Could not fetch weather data for ${location}`);
  }
}

function processWeatherData(weatherData) {
  let currentWeather = weatherData.currentConditions;
  let processedWeatherData = new Map();
  processedWeatherData.set("location", weatherData.resolvedAddress);
  processedWeatherData.set("temp", currentWeather.temp);
  processedWeatherData.set("description", weatherData.days[0].description);
  processedWeatherData.set("icon", currentWeather.icon);
  processedWeatherData.set("windSpeed", currentWeather.windspeed);
  processedWeatherData.set("humidity", currentWeather.humidity);

  return processedWeatherData;
}

function displayWeatherData(weatherData) {
  weatherLocation.textContent = weatherData.get("location");

  weatherTemp.innerHTML = "";
  weatherTemp.appendChild(getIcon("temp"));
  weatherTemp.appendChild(getSpan(weatherData.get("temp") + "Â°C"));

  weatherIcon.innerHTML = "";
  weatherIcon.appendChild(getIcon(weatherData.get("icon")));

  weatherDescription.textContent = weatherData.get("description");

  weatherHumidity.innerHTML = "";
  weatherHumidity.appendChild(getIcon("humidity"));
  weatherHumidity.appendChild(getSpan(weatherData.get("humidity") + "%"));

  weatherWindSpeed.innerHTML = "";
  weatherWindSpeed.appendChild(getIcon("windSpeed"));
  weatherWindSpeed.appendChild(getSpan(weatherData.get("windSpeed") + "km/h"));
}

searchButton.addEventListener("click", () => {
  let location = locationInput.value;
  if (location != "") {
    getWeatherData(location);
  }
});

